INSERT INTO `ezcontentobject_attribute` VALUES(0, 186, 85, 0, 1045487555, '<?xml version="1.0" encoding="utf-8"?>\n<section xmlns:image="http://ez.no/namespaces/ezpublish3/image/" xmlns:xhtml="http://ez.no/namespaces/ezpublish3/xhtml/" xmlns:custom="http://ez.no/namespaces/ezpublish3/custom/"><paragraph>The <emphasize>StringBuilder</emphasize> class has exactly the same API as the&amp;nbsp;<emphasize>StringBuffer</emphasize> class, except <emphasize>StringBuilder</emphasize> is not thread safe. In other words, its&amp;nbsp;methods are not synchronized. It is&amp;nbsp;recommended that you use <emphasize>StringBuilder</emphasize> instead of <emphasize>StringBuffer</emphasize> whenever possible&amp;nbsp;because <emphasize>StringBuilder</emphasize> will run faster. So apart from&amp;nbsp;synchronization, anything we say about <emphasize>StringBuilder''s</emphasize> methods holds true for&amp;nbsp;<emphasize>StringBuffer''s</emphasize> methods, and vice versa.</paragraph></section>\n', 'ezxmltext', 379, 'eng-US', 2, 0, '', 1);
